	public void tabTele(int x, int y, int item) {
		lastAction = System.currentTimeMillis();
		resetWalkingQueue();
		if(action == 1) {
			say("you can't tele away from jail");
			return;
		}
		if(tabId != -1)
			tabId = -1;

		tabId = item; 

		tX = x;
		tY = y;
		tH = 0;

		tStage = 6;
		tTime3 = 0;

		followID = 0;
		followID2 = 0;
		ResetAttack();
		ResetAttackNPC();
	}
