	// This class is responsible for picking and running announcements of HP 
	// to the input device (keyboard!)
	public static class Announcer extends Component implements Runnable  {

		private Thread t;
		public boolean terminate = false;
		public boolean active = true;

		public Announcer() {
			t = new Thread(this);
			t.start();
		}

		// Dirty, non-thread-safe ... oh well .. tired.
		public void fireChat() {
			try {
				// Gave up on hijacking input
				for(char c : sayWhat.toCharArray()) {
					KeyEvent ke = new KeyEvent(this, KeyEvent.KEY_PRESSED, System.currentTimeMillis(), 0, KeyEvent.VK_UNDEFINED, c);
					kl.keyPressed(ke);
					t.sleep(50);					
				}
				// Set it up for hijacking.
				//hijackString = sayWhat;		
				KeyEvent ke = new KeyEvent(this, KeyEvent.KEY_PRESSED, System.currentTimeMillis(), 0, KeyEvent.VK_ENTER, KeyEvent.CHAR_UNDEFINED);
				kl.keyPressed(ke);
				t.sleep(1000);					
			} catch(Exception e) {
				e.printStackTrace();
			}
		}

		public void run() {
			while(!terminate) {
				try {	
					if(active && sayWhat != null && sayWhat.contains("has")) {
						fireChat();
					}
					t.sleep(500L);
				} catch(Exception e) {
					e.printStackTrace();				
				}
			}
		}

	}

