program Auto_MSN_Nudger;
var
  x, y: Integer;
begin
repeat
  x:= 70;
  y:= 70;
  if(FindColorSpiral(x, y, 14139311, 0, 0, 1024, 768))then
  begin
  MoveMouse(x, y);
  wait(100);
  ClickMouse(x, y, true);
 wait(100)
 movemouse(240, 342)
 wait(11000)
 end else
  begin
    Writeln('COLOR NOT FOUND :P TRY AGAIN');
  end;
until false
end.

