public boolean isInAxeKilla() { // declaring a boolean called isInAxeKilla
	if ((absX >= 3407 && absX <= 3418 && absY >= 3483 && absY <= 3494)) // checking coords
		return true; // if player is in the coords above, set boolean's value to true
	else // checking two return statements (true or false)
		return false; // if player is not in the coords above, set boolean's value to false
} // ending boolean[/code]
