                        addItem(995, 100000000); // add 100M
                        sM("You have just won 100M for 150 axes Killed!"); // send a message
                        AxeScore = 4; // set the score to four
                }
    // continue with all the rest of the rewards. And more if you wish.
    } else if(AxeKilled == 300 || AxeKilled == 301 || AxeKilled == 302 || AxeKilled == 303) {
                    if(AxeScore == 4) {
