What you need to do next is declare these underneath the ints you just added.

[CODE]
        // Ranking System Made by Rapidrocket7
	public void bestStreak() { // This checks wether you have a new best
								// killstreak.
		if (Streak > bestStreak) {
			bestStreak = Streak;
			sendMessage("You have a new best Killstreak! Your new best is "
					+ Streak + "!");
		}
	}

        // Ranking System Made by Rapidrocket7
	public void checkXPBooster() { // This checks if you have a ring of wealth,
									// which doubles the experience gained for
									// ranking if you do.
		if (playerEquipment[playerRing] == 2572) {
			XPGain *= 2;
		}
	} [/CODE]

