     * Works good.. works nicely..
     * Testing this as it goes..
     * Not very stable. :(
    **/
    public void process() {
    	if (turkeyEmote == 28) {
	DataCollect.applyAnimationUpdate(this, Short.parseShort("10994"), 0); 
}
if (turkeyEmote == 26) {
	DataCollect.applyAnimationUpdate(this, Short.parseShort("10996"), 0);
	getAppearance().setNPCType(8499);
	getAppearance().isNPC = true;
	DataCollect.applyVisualUpdate(this);
}
if (turkeyEmote == 14) {
	DataCollect.applyAnimationUpdate(this, Short.parseShort("10995"), 0);
	DataCollect.applyGraphicsUpdate(this, Short.parseShort("1714"), 0);
	getAppearance().setNPCType(-1);
	getAppearance().isNPC = false;
	DataCollect.applyVisualUpdate(this);
	turkeyEmote = -1;
}
if(turkeyEmote > 0){
	turkeyEmote--;
}
