package com.feather.game.player.dialogues.quests;

import com.feather.game.player.Player;
import com.feather.game.player.Skills;

/**
 * Created by Arham 4 on 8/9/14.
 */
public class BigChompyBirdHunting {

    /**
     * Sends the configuration to the quest tab and sets Doric's Quest into progress.
     *
     * @param player
     */
    public static void handleProgressQuest(final Player player) {
        player.startedBigChompyBirdHunting = true;
        player.inProgressBigChompyBirdHunting = true;
        player.getPackets().sendConfig(293, 1);
        player.getInterfaceManager().sendInterfaces();
        player.getPackets().sendUnlockIComponentOptionSlots(190, 15, 0, 201, 0, 1, 2, 3);
    }

    /**
     * The interface before the player has started the quest.
     *
     * @param player
     */
    public static void handleQuestStartInterface(final Player player) {
        player.getInterfaceManager().sendInterface(275);
        player.getPackets().sendRunScript(1207, 3);
        player.getPackets().sendIComponentText(275, 1, "Big Chompy Bird Hunting");
        player.getPackets().sendIComponentText(275, 10, "");
        player.getPackets().sendIComponentText(275, 11, "<col=330099>I can start this quest by speaking to the ogre</col> <col=660000Rantz</col> <col=330099>who</col>");
        player.getPackets().sendIComponentText(275, 12, "<col=330099>lives <col=660000>East</col> <col=330099>of the</col> <col=660000>Ogre City</col><col=330099>,</col> <col=660000>North</col> <col=330099>of the</col> <col=660000>Swamp Pool.</col>");
        player.getPackets().sendIComponentText(275, 13, "<col=330099>To complete this quest I need:</col>");
        player.getPackets().sendIComponentText(275, 14, (player.getSkills().getLevel(Skills.FLETCHING) > 5 ? "<str>" : "<col=330099>") + "Level 5 Fletching");
        player.getPackets().sendIComponentText(275, 15, (player.getSkills().getLevel(Skills.COOKING) > 30 ? "<str>" : "<col=330099>") + "Level 30 Cooking");
        player.getPackets().sendIComponentText(275, 16, (player.getSkills().getLevel(Skills.RANGE) > 30 ? "<str>" : "<col=330099>") + "Level 30 Ranging");
        if (player.getSkills().getLevel(Skills.FLETCHING) > 5 && player.getSkills().getLevel(Skills.COOKING) > 30 && player.getSkills().getLevel(Skills.RANGE) > 30) {
            player.getPackets().sendIComponentText(275, 17, "<col=330099>I have all the</col> <col=660000>stats</col> <col=330099>needed to complete this quest.</col>");
        } else {
            player.getPackets().sendIComponentText(275, 17, "");
        }
        for (int i = 18; i < 300; i++) {
            player.getPackets().sendIComponentText(275, i, "");
        }
    }

    /**
     * The interface during the quest when the player gathers the items.
     *
     * @param player
     */
    public static void handleProgressQuestInterface(final Player player) {
        player.getInterfaceManager().sendInterface(275);
        player.getPackets().sendRunScript(1207, 3);
        player.getPackets().sendIComponentText(275, 1, "Big Chompy Bird Hunting");
        player.getPackets().sendIComponentText(275, 10, "");
        player.getPackets().sendIComponentText(275, 11, "<str><col=330099>I can start this quest by speaking to the ogre</col> <col=660000Rantz</col> <col=330099>who</col>");
        player.getPackets().sendIComponentText(275, 12, "<str><col=330099>lives <col=660000>East</col> <col=330099>of the</col> <col=660000>Ogre City</col><col=330099>,</col> <col=660000>North</col> <col=330099>of the</col> <col=660000>Swamp Pool.</col>");
        player.getPackets().sendIComponentText(275, 13, "<col=330099>I agreed to get</col> <col=660000>Rantz</col> <col=330099>some</col> <col=660000>'stabbers'</col>");
        for (int i = 14; i < 300; i++) {
            player.getPackets().sendIComponentText(275, i, "");
        }
    }
