<blockquote>{.Script Info:
# ScriptName  = 99 Beers on the Wall
# Author      = TexasMd91
# Description = Singles the Beers on the wall song.
# Version     = 1
# Date        = Friday, March 16, 2007
# Comments    =
|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
|   INSTRUCTIONS:                                                  
|   1. Set the 3 values under "const"                           
|   2. Open up runescape.                                         
|   3. Login and move the crosshair to the rs window     
|   4. Press "Run" and watch it sing the song!               
|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 ````````````````````````````````````````````````````
/Script Info}

[b]program[/b] BeerOnTheWall;

[b]var[/b]
  Bottles: integer;

[b]const[/b]
  TotalBottles = 100; //total amount of bottles to start with
  SecondsToWait = 3; //how many seconds between each line
  Prefix = 'white:'; //white:, red:, flash1:, wave2:, etc.

  Message1 = ' bottles of beer on the wall, ';
  Message2 = ' bottles of beer.';
  Message3 = 'Take one down and pass it around, ';
  Message4 = ' bottles of beer on the wall.';

[b]procedure[/b] Beer;
begin
  [b]if[/b] Bottles = 0 [b]then[/b]
    Bottles := TotalBottles;

  wait(SecondsToWait * 1000);
  sendkeys(Prefix + IntToStr(Bottles) + Message1 + IntToStr(Bottles) + Message2 + chr(13));
  wait(SecondsToWait * 1000);
  Bottles := Bottles - 1;
  sendkeys(Prefix + Message3 + IntToStr(Bottles) + Message4 + chr(13));
[b]end;[/b]
[b]
begin[/b]
[b]  repeat[/b]
    Beer;
  [b]until[/b] (false)
[b]end.[/b]</blockquote>

