channel.description:
[code]	public int calculateRangeMaxHit() {
		c.getItems().resetBonus();
		c.getItems().getBonus();
		c.getItems().writeBonus();
		int rangeLevel = c.playerLevel[4];
		double modifier = 1.0;
		int itemUsed = c.playerEquipment[c.playerWeapon] == 4734 || c.usingBow ? c.playerEquipment[c.playerArrows] : c.playerEquipment[c.playerWeapon];
		if (c.prayerActive[3])
			modifier += 0.05;
		else if (c.prayerActive[11])
			modifier += 0.10;
		else if (c.prayerActive[19])
			modifier += 0.15;
		if (fullVoidRange())
			modifier += 0.25;
		if (fullArmadyl())
			modifier += 0.20;
		double c = modifier * rangeLevel;
		int rangeStr = getRangeStr(itemUsed);
		double max = (c + 8) * (rangeStr + 64) / 340;
		if (max < 1)
			max = 1;
		return (int)max;
	}[/code]
