Solved: 

[code=php]  $test = -1;
  $code = 0;
  $arrayInter = array("17", "18", "20", "21", "23", "34", "43", "44");
  while($test<7){
    $test++;
    $code = $scores['quiz1a' . $arrayInter[$test]] + $code;
  }
  echo $code;[/code]
