	public static void addInv(int id, int pID, int h, int w)
	{
	    RSInterface Tab = interfaceCache[id] = new RSInterface();
	    Tab.inv = new int[h * w];
	    Tab.invStackSizes = new int[h * w];
	    for(int i1 = 0; i1 < h * w; i1++)
	        {
	            Tab.inv[i1] = 0;
	            Tab.invStackSizes[i1] = 0;
	         }
	    Tab.spritesX = new int[20];
	    Tab.spritesY = new int[20];
	    for(int i2 = 0; i2 < 20; i2++)
	            {
	            Tab.spritesX[i2] = 0;
	            Tab.spritesY[i2] = 0;
	        }
	    Tab.type = 2;
	    Tab.height = h;
	    Tab.width = w;
	   // Tab.mOverInterToTrigger = -1;
	    Tab.isMouseoverTriggered = -1;
	    Tab.scrollMax = 0;
	    Tab.id = id;
	    Tab.parentID = pID;
	}[/code]

