Procedure Setup2;
 begin
  setupsrl;
  highestangle;
  MakeCompass('N');
  FindEnt(WC1);
  FindEnt(WC2);
  FindEnt(WC3);
  findnormalrandoms;
  Findmaze;
  findmime;
  findlamp('Woodcutting');
  FindHead;
  AttachHead;
  TalkToRand;
  DwarfItem;
  SolvePinBall;
  FindScapeRune;
  SolveFrog;
  If (FindFight) Then
  Runawaydirection('N')
  Wait(25000+Random(5000))
  RunBack;
  If (Finddead) then
  RandomNextPlayer(True);
 end;

