Procedure Notban;
 var
 NoBan : Integer;
  begin
   if ( not ( LoggedIn )) then Exit;
   NoBan:=Random(50)
    case NoBan of
     1: RandomRClickEvery(2 + Random(15));
     2: hoverskill('woodcutting', false);
     3: hoverskill('random', false);
     4: DoEmote(1 + Random(20));
     5: BoredEvery(9 + Random (21));
     6: PickUpMouse;
     7: LeaveScreenEvery(10 + Random(5));
     8: RotateEvery(20 + Random(5));
    end;
   end;


Procedure NexttotreeCheck;
 var FishSymbol, x, y: Integer;
 begin
  FishSymbol := BitmapFromString(21, 17, 'z78DAEDD5D10A80200C' +
       '05D05FDA0A713D9AD1FF7F528485A33529B6822085FB50D2B9052' +
       '9C03F2E0F6CCE87C544298E414BD601BDDCB6A836A91DF04D57F9' +
       '1A5FD01517724AF39E18E2DCEBE2F1EE8D0E42E7A24C4D27A2387' +
       '8E89B5566AB03EF69D7013B9A0E2975B9664DBF773F7FFEBA2E43' +
       'C962ED570CBADAA15A5CE4C9DBD63E7E7F9CB4B434E9F6BDCE6BE' +
       '7BFBBCFBF72E2D8CFB8053C63B21E');
      if(findbitmap(FishSymbol, x, y))then
      MakeCompass('N');
      WriteLn('Found the tree spot.');
      Setup2;
 end;

Procedure Findandchop;
var x,y :integer;
begin
  Notban;
  setup2;
  Responder;
  If (FindFight) Then
   begin
     Runawaydirection('N')
     Wait(25000+Random(5000))
     RunBack;
   end;
  if findcolorspiral(x,y,WC1,3,3,515,336)or
     findcolorspiral(x,y,WC2,3,3,515,336)or
     findcolorspiral(x,y,WC3,3,3,515,336) then
   begin
     Notban;
     Responder;
     Case Random(20) of
       0:TypeSend('lalala');
       1:TypeSend('Yes WC lvl!');
       2:TypeSend('Hey Hey everyone');
       3:TypeSend('700 more to go....');
       4:TypeSend('Bored....');
       5:TypeSend('Almost there!');
       6:TypeSend('he he he');
       7:TypeSend('I whish i was a higher lvl');
     end;
     mmouse(x,y,2,2)
     if(IsUpTextMulti('Chop','op','Ch')) then
      getmousepos(x,y);
      Mouse(x,y,0,0,true)
      Begin
        Case random(3) of
          1: hoverskill('woodcutting', false);
          2: PickUpMouse;
          3: LeaveScreenEvery(10 + Random(5));
        end;
      end;
      wait((WAITTIME*1000)+Random(500))
      if not loggedin then
       RandomNextPlayer(True);
      L := L*28;
      T := T+1;
   end;
end;

Procedure Bank;
  var BankSymbol, x, y: Integer;
begin
  Setup2;
  Notban;
  Responder;
  BankSymbol := BitmapFromString(22, 24, 'z78DAED95D10A80200C' +
       '457F494B141F53F3FF3FA960040B59AD74F69057B844A0F7C0E65' +
       '46A68A8424EE97D51DE2537DA90C31A9C8FF3C9E1BF080FCA2DD3' +
       'D79CB449373CC2E9A5BB688D9A16EBB33F4804187016FEC6DE80A' +
       '148E730402E76BCF7715D5E318097BB2418A87EA06AD1968122A1' +
       'EA22C7C0EF8D2A06C6DDE433544D89CBBBC9EF87B6930A1870169' +
       '50E2E31B1C1E17C6A2EF57FBFBABE9B43DFE8A735DD006D2B7F13' +
       '');
  if(findbitmap(BankSymbol, x, y))then
   wait(3000)
  Begin
    Case random(4) of
      1: MMouse(x,y,3,3)
      2: MMouse(X,y,4,4)
      3: MMouse(x,y,5,5)
      4: MMouse(x,y,6,6)
    end;
    getmousepos(x,y);
    Mouse(X,y,3,3,true)
    wait(7000)
    if findcolorspiral(x,y,BC1,3,3,515,336)or
       findcolorspiral(x,y,BC2,3,3,515,336)or
       findcolorspiral(x,y,BC3,3,3,515,336) then
     MMouse(X,y,3,3)
    getmousepos(x,y);
    Mouse(X,y,3,3,true)
    if(IsUpTextMulti('Bank','Ba','nk')) then
     Getmousepos(x,y);
     Mouse(x,y,0,0,true)
    if findcolorspiral(x,y,LC1,3,3,515,336)or
       findcolorspiral(x,y,LC2,3,3,515,336)or
       findcolorspiral(x,y,LC3,3,3,515,336) then
     getmousepos(x,y);
     Mouse(X,Y,3,3,true)
    getmousepos(x,y);
    Mouse(X,y,3,3,true)
    if(IsUpTextMulti('Deposit All', 'All', 'Al')) then
     getmousepos(x,y);
     Mouse(X,y,0,0,false)
    Begin
      Case random(50) of
        1: hoverskill('woodcutting', false);
        2: PickUpMouse;
        3: LeaveScreenEvery(10 + Random(5));
        4:TypeSend('lalala')
        5:TypeSend('Yes WC lvl!')
        6:TypeSend('Hey Hey everyone')
        7:TypeSend('700 more to go....')
        8:TypeSend('Bored....')
        9:TypeSend('Almost there!')
        10:TypeSend('he he he')
        11:TypeSend('I whish i was a higher lvl')
      end;
      begin
        if not loggedin then
         RandomNextPlayer(True);
      end;
    end;
  end;
end;

Procedure ProgressReport;
 begin
  WriteLn('==================================');
  WriteLn('Thanks for using my WillowChopper.');
  WriteLn('It has cut' + inttostr(l) + 'Loads.');
  WriteLn('It has done' + inttostr(t) + 'Trips.');
  WriteLn('==================================');
 end;
begin
repeat
DeclarePlayers;
Printout;
Updates;
LoadBTMPS;
Setup2;
Nexttotreecheck;
FindandChop;
Bank;
ProgressReport;
until(L>Tri*27);
end.

