Program Typeit;

{.include srl/srl.scar}
{.include srl/srl/extended/xtext.scar}
{.include SRL/SRL/misc/Loading.scar}
{.include SRL/SRL/misc/Trade.scar}

Const
  Text1 = 'he';             // what you want it to type
  Text2 = 'is';            // what you want it to type
  Text3 = 'dfg';           // what you want it to type
  Text4 = 'sdfg';          // what you want it to type
  Text5 = 'dfg';         // what you want it to type
  HowManyTimes = 10; //  it will say all 5 things
  WaitTime = 5;     // How long to wait in millaseconds

Var
  a : Integer;
  tOrder : Array[0..4] of String;

Procedure RandomOrder;
Begin
  tOrder[0] := Text1;
  wait(waitTime*900);
  tOrder[1] := Text2;
  wait(waitTime*900);
  tOrder[2] := Text3;
  wait(waitTime*900);
  tOrder[3] := Text4;
  wait(waitTime*900);
  tOrder[4] := Text5;
  wait(waitTime*900);
End;

procedure Signature;
begin
  ChangeReportWidth(50);
  Writeln('   _/\_____________      _____      _      ___/\_______/\_________/\__________ ');
  Writeln('  /       /|  | \  \    /  /  \    / \    |  ____  |  ____  |   ___   |  _____|');
  Writeln(' /   ____/ |  |  \  \  /  /    \  /   \   | |    | | |    | |  |   |  | |      ');
  Writeln(' \      \  |  |   \  \/  //  _  \/ _   \  | |    | | |    | |  |___|  | |___   ');
  Writeln('  /___   \ |  |__  \_  _//   /\_  _/\   \ | |    | | |    | |   _   __|  ___|  ');
  Writeln(' /       / |     |  |  |/   /   \/   \   \| |____| | |____| |  | \  \ | |_____ ');
  Writeln('/_____  /  |_____|  |  |___/          \___|___  ___|___  ___|  |  \__\|_  ____|');
  Writeln('      \/             \/                       \/       \/    \/         \/     ');
  Writeln('    Presents :');
  Writeln('             An Autotyper: My First Script');
end;

//------------------------------------------------------------------------\\
{                Main Loop--lol not really it is very short                ||
\\------------------------------------------------------------------------//}
Begin
  Signature;
  SetupSRL;
  ActivateClient;
  RandomOrder;
  Repeat;
    SendMyText(tOrder[Random(4)]);
    ClearDebug;
    a := a + 1;
    WriteLn('We Typed '+IntToStr(a)+' time(s).');
  Until (a = HowManyTimes)or(IsFKeyDown(3));
end.[/code][br][size=1]Posted on: May 24, 2007, 07:25:38 PM[/size][hr]hi
