[code]
import java.io.*;
public class BoredomClass {

public static boolean bored;
public static boolean print = true;
public static boolean checkBoredStatus() {
	return bored ? true : false;
}
public static int boredom[] = {0, 1};
public static int randomBoredom() {
	return boredom[(int)(Math.random()*boredom.length)];
}
		public static void main (String[] args) {
			bored = randomBoredom != 0;
			System.out.println("Boredom is "+checkBoredStatus());
		}
}
