procedure bowselection;
begin
if(bowtype = 'long')then
makelongbows;
if(bowtype = 'short')then
makeshortbows;
end;

procedure getlogs;
begin
     OpenBank;
     repeat
     FixBank;
     Withdraw(1,1,27);
     CloseWindow;
     bowselection;
     until(false)
end;

procedure setup;
begin
LoadItems;
GameTab(2);
GameTab(4);
MakeCompass('n');
ChatsOff;
getlogs;
end;

