UseFood: Boolean;

Procedure Setup;
begin
{ User Setup}
User:='username';
Pass:='password';
FightMode:=1;
AutoLogin:=true;
{ Monster Setup }
Color1:=7028284;        // Color Of Monster1 Here
Color2:=5517358;        // Color Of Monster2 Here
Color3:=3940898;        // Color Of Monster3 Here
tol:=10;                // Tolerance to find the monster color. 10 is default
{ Food Setup }
UseFood:=False
FoodColor:=000000002
HealWhen:=4             //2=20HP, 3=30HP,4=40HP ect

End;

procedure FindRandoms;
begin
  RotateEvery(1+random(5))
  FindNormalRandoms;
  ClickToContinue;
  CloseWindow;
  FindLamp('mining');
  FindDead;
  FindMod;
  FindMime;
  FindMaze;
  FindQuiz;
  FindFrog;
  FindDoctor;
  FindDemon;
  if Findname Then FindTalk;
end;

Procedure OpenFriends;
Begin
 Mouse(584,485,5,5,true)
 Wait(300+random(300))
End;

Procedure AntiBan1;
Begin
 ABMouse:=True
 Status('AntiBan Activated!')
 ABCase1:=Random(5)
 ABCase1:=Random(5)
 Case ABCase1 Of
  1:
   Begin
    MMouse(586,310,21,21)
    Wait(100+Random(100))
    MMouse(246,98,40,40)
    Wait(100+Random(100))
    FindRandoms;
   End;
  2:
   Begin
    Status('Checking Stats!')
    Mouse(586,184,7,7,true)
    Wait(20+Random(20))
    GetMousePos(x,y)
    MMouse(x,y,2,2)
    Wait(125+Random(125))
    GetMousePos(x,y)
    MMouse(x,y,2,4)
    Wait(1000+Random(1000))
    FindRandoms;
   End;
  3:
   Begin
    MMouse(600,276,20,12)
    Wait(45+Random(25))
    MMouse(616,283,12,17)
    Wait(45+Random(25))
    MMouse(614,423,12,17)
    Wait(190+Random(225))
    Mouse(559,186,7,7,True)
    Wait(500+Random(500))
    GetMousePos(X,Y)
    Wait(20+Random(5))
    MMouse(x,y,2,6)
    Mouse(584,187,2,2,true)
    Wait(1000+random(200))
    FindRandoms;
   End;
  4:
   Begin
    CMMouse(585,187,5,5)
    Wait(1+random(20))
    GetMousePos(x,y)
    Mouse(x,y,0,0,true)
    Wait(120+random(120))
    CMMouse(588,265,20,20)
