This works on the same principles as a RSPS, as it hosts a server at your IP (and a port of your choosing), and allows my client to connect to it from anywhere with internet access. 
[img]http://img6.imageshack.us/img6/5772/clienthj.png[/img]
NOTE: I do not show all code classes, only the important ones.

[u][b]ChatServer.java[/b][/u]
[code]import java.io.*;
import java.net.*;
import java.util.*;

public class ChatServer extends ConnectionManager {
	public boolean serverRunning = true;
	ServerSocket serverSocket = null;
	public static User[] users = new User[500];
	public String splitter = "\\|\\|\\|\\|";
	public ChatServer(int port) throws Exception {
		try {
			serverSocket = new ServerSocket(port);
			System.out.println("ChatServer started on port: "+port);
		}
		catch (IOException e) {
			System.out.println("Could not start server on port: " +port);
			System.exit(-1);
		}
	}

	public void run() {
		Socket socket = null;
		while (serverRunning) {
			try {
				socket = serverSocket.accept();
				socket.setTcpNoDelay(true);
				System.out.println("Connection Accepted from: " + getAddress(socket));
				int id = getFreeId();
				if (id != -1) {
					users[id] = new User(socket, id, this);
					sockets[id] = socket;
				}
				else {
					socket.close();
				}
				String filename = ("./BannedHosts/" + getAddress(socket) + ".dat");
				File banned = new File(filename);
				if (banned.exists()) {
					try {
						users[id].rwSocket.write("09You have been banned from this server.\nPlease refer to the hoster for more details.");
					}
					catch (Exception e) {
					}
					try {
						socket.close();
						users[id].destruct();
					}
					catch (IOException e) {
						e.printStackTrace();
					}
					sockets[id] = null;
					users[id] = null;
				}
				else {
					outputAll("14" + users[id].username + splitter + "11" + users[id].username + " has joined the channel!");
					setUserList();
				}
			}
			catch (IOException e) {
				System.out.println("Client accept failed.");
				System.exit(-1);
			}
		}
	}

	public int getFreeId() {
		for (int i = 0; i < sockets.length; i++) {
			if (sockets[i] != null) {
				continue;
			}
			return i;
		}
		return -1;
	}

	public void setUserList() {
		String userList = (splitter+"16");
		for(User u : users) {
			if(u != null && u.username.length() > 0) {
				userList += (u.username + "\"");
			} 
		}
		for(User u : users) {
			try {
				u.rwSocket.write(userList);
			}
			catch(Exception e) {
			}
		}
	}

	public void outputAll(String message) {
		for(User u : users) {
			if(u != null && getAddress(u.socket) != "0.0.0.0") {
				try {
					u.rwSocket.write(message);
				}
				catch(Exception e) {
				}
			}
		}
	}

