	public void changeUser(String user, String newUser) {
		for(int i = 0; i < userList.getModel().getSize(); i++) {
    			if(user.equals((String)userList.getModel().getElementAt(i))) {
				users.remove(i);
				users.add(i, newUser);
				userList.setListData(users.toArray());
				return;
			}
		}   
    	}

	public void addUser(String user) {
		 	users.add(user);
			userList.setListData(users.toArray());	
	}

	public void setUserList(String usersd) {
		blank.add("");
		userList.setListData(blank.toArray());
		blank.remove(0);
		String[] userListd = usersd.split("\""); 
		users.clear();
		for(int i = 0; i < userListd.length; i++) {
			if(userListd[i] != null && userListd[i].length() > 0) {
				users.add(userListd[i]);
			}
		}
		userList.setListData(users.toArray());
	}
}
