[code]public static String findcachedir() {
        String as[] = {"c:/"};
        if(storeid < 32 || storeid > 34)
            storeid = 32;
        String s = "ancientxcache";
        for(int i = 0; i < as.length; i++)
            try {
                String s1 = as[i];
                if(s1.length() > 0) {
                    File file = new File(s1);
                    if(!file.exists())
                        continue;
                }
                File file1 = new File(s1 + s);
                if(file1.exists() || file1.mkdir())
                    return s1 + s + "/";
            }
            catch(Exception _ex) { }
        return null;
    }[/code]
