			if((player.flag != null) && (player.flag.length() == 2)) {
				player.getActionSender().sendMessage("You can only set your flag once!");
				return;
			}
			String flg = args[0].trim().toUpperCase();
			if(Flags.flags.contains(flg)) {
				player.flag = flg;
				player.setAppearnceChanged(true);
