	public void drawPicture(int x, int y, Sprite picture) {
		try {
			int l = x + y * menuDefaultWidth;
			int i1 = 0;
			int j1 = picture.getHeight();
			int k1 = picture.getWidth();
			int l1 = menuDefaultWidth - k1;
			int i2 = 0;
			if (y < imageY) {
				int j2 = imageY - y;
				j1 -= j2;
				y = imageY;
				i1 += j2 * k1;
				l += j2 * menuDefaultWidth;
			}
			if (y + j1 >= imageHeight)
				j1 -= ((y + j1) - imageHeight) + 1;
			if (x < imageX) {
				int k2 = imageX - x;
				k1 -= k2;
				x = imageX;
				i1 += k2;
				l += k2;
				i2 += k2;
				l1 += k2;
			}
			if (x + k1 >= imageWidth) {
				int l2 = ((x + k1) - imageWidth) + 1;
				k1 -= l2;
				i2 += l2;
				l1 += l2;
			}
			if (k1 <= 0 || j1 <= 0)
				return;
			byte byte0 = 1;
			if (f1Toggle) {
				byte0 = 2;
				l1 += menuDefaultWidth;
				i2 += picture.getWidth();
				if ((y & 1) != 0) {
					l += menuDefaultWidth;
					j1--;
				}
			}
			method235(imagePixelArray, picture.getPixels(), 0, i1, l, k1, j1, l1, i2, byte0);
		}
		catch (Exception e) {
			System.err.println("Error drawing: " + picture);
			e.printStackTrace();
			System.exit(1);
		}
	}
