$checkit = mysql_num_rows($echeck);
if ($checkit!=0)
{
die("Your Email Address ($email) is currently active!");
}
$ucheck = mysql_query("SELECT username FROM users WHERE username='$username'");//checks if your username is taken
$ucheckit = mysql_num_rows($ucheck);
if ($ucheckit!=0)
{
die("The username ($username) is currently active!");
}
if ($username&&$pass1&&$pass2&&$email)//checks if all of the fields are filled out
{
if($pass1!=$pass2)//checks if passwords are the same
{
echo"Your passwords are not the same";
}
if ($pass1==$pass2)
{
if(strlen($username)>25)//if the username has over 25 characters in it
{
echo"Your Username ($username) Is Too Long Please Have It Less Then 25 Characters.";
}
else
{
if(strlen($username)<2)//not enough characters
{
echo"Your username is too short.";
}
else
{
if(strlen($email)>120 or strlen($email)<4)//character count again...
{
echo"Please Enter A Valid Email Address.";
}
else
{
if(strlen($pass1)>30 or strlen($pass1)<4)
{
echo "Your password must be over 3 characters and under 30";
