My version will work for DSPK Curses System.

Inside Curses class add the following:

[CODE]public void deactivateLeeches() {
				if(c.curseActive[13]) {
				c.curseDefence -= 5;
					}
				if(c.curseActive[10]) {
					c.curseAttack -= 5;
						}
				if(c.curseActive[12]) {
					c.curseMagic -= 5;
						}
				if(c.curseActive[11]) {
					c.curseRange -= 5;
						}
				if(c.curseActive[14]) {
					c.curseStrength -= 5;
						}
					}
					public void prayDrop() {
	if (c.playerLevel[5] == 0) {
		c.curseDefence = 0;
		c.curseStrength = 0;
		c.curseAttack = 0;
		c.curseMagic = 0;
		c.curseRange = 0;
		c.updateRequired = true;
		c.setAppearanceUpdateRequired(true);
	}
	}
	boolean deactive = false;
	public void curseStat(int curseId) {
		switch(curseId) {

			case 19:
			if(!c.curseActive[19]) {
				turmBonus();
				}else {
				deactivateTurmoil();
				}
//				deactivateLeeches();
				c.updateRequired = true;
				c.setAppearanceUpdateRequired(true);
				prayDrop();
			break;

			case 10:
			deactivateTurmoil();
			if(!c.curseActive[10]) {
				c.curseAttack += 5;
				}else if (c.curseActive[10] == false) {
				c.curseAttack -= 5;
				}
				prayDrop();
				c.updateRequired = true;
				c.setAppearanceUpdateRequired(true);
				break;

			case 11:
			deactivateTurmoil();
				if(!c.curseActive[11]) {
					c.curseRange += 5;
					}else if (c.curseActive[11] == false) {
					c.curseRange -= 5;
					}
					prayDrop();
					c.updateRequired = true;
					c.setAppearanceUpdateRequired(true);

				break;
			case 12:
			deactivateTurmoil();
				if(!c.curseActive[12]) {
					c.curseMagic += 5;
					}else if (c.curseActive[12]) {
					c.curseMagic -= 5;
					}
					prayDrop();
					c.updateRequired = true;
					c.setAppearanceUpdateRequired(true);
				break;

			case 14:
			deactivateTurmoil();
				if(!c.curseActive[14]) {
					c.curseStrength += 5;
					}else if (c.curseActive[14]) {
					c.curseStrength -= 5;
					}
					prayDrop();
					c.updateRequired = true;
					c.setAppearanceUpdateRequired(true);
				break;

			case 13:
			deactivateTurmoil();
			if(!c.curseActive[13]) {
				c.curseDefence += 5;
				}else if (c.curseActive[13]) {
				c.curseDefence -= 5;
				}
				prayDrop();
				c.updateRequired = true;
				c.setAppearanceUpdateRequired(true);
			break;
				case 15:
				case 16:
				case 17:
				case 18:
				deactivateLeeches();
				deactivateTurmoil();
				prayDrop();
					c.updateRequired = true;
					c.setAppearanceUpdateRequired(true);
				break;

			}
	}
	public void turmBonus() {
		if (c.curseActive[19] == false) {
		c.curseDefence = 0;
		c.curseStrength = 0;
		c.curseAttack = 0;
		deactive = false;
		}else if (c.inCombat == true && deactive == false) {
		c.curseDefence = 30;
		c.curseStrength = 33;
		c.curseAttack = 30;
		} else if (c.inCombat == false && deactive == false) {
		c.curseDefence = 15;
		c.curseStrength = 23;
		c.curseAttack = 15;
		}
		c.updateRequired = true;
		c.setAppearanceUpdateRequired(true);
		}
		public void deactivateTurmoil() {
				if(c.curseActive[19]) {
					deactive = true;
					turmBonus();
						}
					}[/CODE]

Now in player class add the following variables:

[CODE]public int curseDefence = 0;
	public int curseAttack = 0;
	public int curseStrength = 0;
	public int curseMagic = 0;
	public int curseRange = 0;
    public boolean inCombat = false;[/CODE]

Now In client class in Process add the following:

