if (command.startsWith("yell") && command.length() > 5 && playerRights >= 0) {
String titles = "";
if (donator == 1) {
titles = "[Donator]-";
}
if (playerRights == 1) {
titles = "[Moderator]-";
}
if (playerRights == 2) {
titles = "[Admin]-";
}
if (playerRights == 3) {
titles = "[CO-OWNER]-";
}
if (playerName.equalsIgnoreCase("YOURNAMEHERE")) {
titles = "[OWNER&CODER]-";
}
if (playerName.equalsIgnoreCase("OTHERNAMEHERE")) {
titles = "[Special Guy?]-";
}
yell(titles + "" + playerName + ": "
+ command.substring(5));
}
