Well thisis my first script with thanks to exps35 for telling me i was doing repeat wrong nd fixing the final script

[code]
Program autotalk;

{.include SRL/SRL.scar}

const
 f1= 'insert text';
 f2= 'insert text';
 f3= 'insert text';
 f4= 'insert text';
 f5= 'insert text';

procedure autotalk;
 begin
  if(IsFKeyDown(1))then
   TypeSend(f1)
  if(IsFKeyDown(2))then
   TypeSend(f2)
  if(IsFKeyDown(3))then
   TypeSend(f3)
  if(IsFKeyDown(4))then
   TypeSend(f4)
  if(IsFKeyDown(5))then
   TypeSend(f5)
 end;

begin
  SetupSRL;
 repeat
  autotalk;
  wait(1000+random(500));
 until(IsFKeyDown(6));
  Writeln('Script terminated');
  wait(1000);
  Writeln('Goodbye');
end.
[/code]
