import java.awt.*;

public class g extends Frame{
   static int z = 100;
   public void paint(Graphics g){
	for(int i = 0; i < 250; i++){
	   for(int j = 0; j < 250; j++){
		if((i % 50 == 0)||(j % 50 == 0)){
		   double r = StrictMath.sqrt(i * i + j * j + z *z);
		   int x = 250 + (int)(500 * j / r);
		   int y = 250 + (int)(500 * i / r);
		   g.drawLine(x , y, x, y);
		}
	   }
	}
   }
   public static void main(String[] args){
	g G = new g();
	G.setSize(500,500);
	G.show();
	for(int i = 0; i < 300; i++){
	   z = 100 + i;
	   G.getGraphics().clearRect(0,0,500,500);
	   G.paint(G.getGraphics());
	}
   }
