[b]Add this[/b]
[code=java]
for (int pickable : pickables.pickableIds) {
				if (objectID == pickable) {
					TurnPlayerTo(objectX, objectY);
					pickables.handlePickables(objectID, objectX, objectY);
				}
			}
[/code]

