[code]
public void getHead(){
    if(ProtMage)
    headIcon = 2;
    if(ProtRange)
    headIcon = 1;
    if(ProtMelee)
    headIcon = 0;
    if(Retribution)
    headIcon = 3;
    if(Redemption)
    headIcon = 5;
    if(Smite)
    headIcon = 4;
    if(isSkulled)
    skullIcon = 0;
    else if(!ProtMage && !ProtRange && !ProtMelee && !Retribution && !Redemption && !Smite)
    headIcon = -1;
}
[/code]
