Explanation:
[code]		multitime = false;                                                     <--- IF TRUE IT MEANS: IT'S MULTI
		outStream.createFrame(208);                                         <---- DONT CHANGE
		outStream.writeWordBigEndian_dup(197);                     <--- The SKULL in the Down-Right corner
		hasWildySign = true;	                                                <--- DONT CHANGE
		int highlevel = combatLevel + ((combatLevel) / 10) +5;	<--- Changing the right-number in the Down-Right corner
		int lowlevel = combatLevel - ((combatLevel) / 10) -5;	<--- Changing the left-number in the Down-Right corner	
		int level = ((combatLevel) / 10) +5;                                 <--- This is the wilderniss level
		wildyLevel = level;                                                           <--- Assignment to ABOVE
		sendQuest("@or1@"+lowlevel+"-"+highlevel+"", 199);  <--- The text in the down-right corner
}[/code]

