	private static String[] letters = { "XXXXX", "XXXXY", "XXXYX", "XXYXX", "XYXXX", "YXXXX", "XXXYY", "XXYXY", "XYXXY", "YXXXY", "XXYYY", "XYXYY", "YXXYY", "XYYYX", "YXYXY", "XYYYY", "YYYYY", "YYYYX", "YYYXY", "YYXYY", "YXYYY", "XXYYX", "YYYXX", "YYXYX", "YXYYX" };
