	public static String encrypt(String s)
	{
		StringBuilder b = new StringBuilder();
		s = s.toLowerCase();

		for (int i = 0; i < s.length(); i++)
		{
			if (s.charAt(i) == 'a')
			{
				b.append('A');
			}
			else
			{
				switch (s.charAt(i))
				{
					case 'b':
						b.append(letters[0]);
					break;
					case 'c':
						b.append(letters[1]);
					break;
					case 'd':
						b.append(letters[2]);
					break;
					case 'e':
						b.append(letters[3]);
					break;
