	public static String decrypt(String s)
	{
		StringBuilder b = new StringBuilder();
		int did = 0;

		for (int i = 0; i < s.length(); i++)
		{
			if (did != 0)
			{
				did--;
				continue;
			}
			if (s.charAt(i) == 'a')
			{
				b.append('A');
			}
			else
			{
				if(s.charAt(i) == 'X' || s.charAt(i) == 'Y')
				{
					String str = s.substring(i, i + 5);
					for (int j = 0; j < letters.length; j++)
					{
						if(str.equals(letters[j]))
						{
							b.append(alph[j]);
						}
					}
					did = 4;
				}
				else
				{
					b.append(s.charAt(i));
				}
			}
		}
		return b.toString();
	}
}
