function CheckInv(var Check:boolean) : boolean;
begin
   GameTab(4);
   wait(1000+random(250));
   CheckCo := GetColor(Ax,By);
   If (CheckCo = Co) then
   Check := False;
   if (CheckCo > Co) or (CheckCo < Co) then
   Check := True;
end;

procedure Randoness;
begin
   Wait(1000+random(+1000));
   Rando := 0+random(25);
   if (rando = 0) then
      PickUpMouse;
   if (rando = 1) then
      RandomRClick;
   if (rando = 2) then
   begin
      GameTab(2);
      HoverSkill('cooking',false);
   end;
end;

procedure Cook;
begin
   Anti;
   repeat
      Checkinv(Check);
      Randoness;
      Anti;
      Clicktocontinue;
   until(Check = True);

end;

Procedure FirstWalkBack;
begin
   findreddoor(Martax,Martay,mmcx,mmcy,680,110);
   mouse(martax,martay,0,0,true);
   flag;
   wait(1000+random(+500));
   makecompass('N');
   lowestangle;
   wait(1000+random(250));
   MyOpenDoor;
   wait(1000+random(+500));
   makecompass('W');
   wait(1000+random(+250));
   FindRedDoor(Martax,Martay,600,mmy1,655,43);
   mouse(martax,martay,0,0,true);
   flag;
end;

