[CODE=java]private  void fillInStream(int forceRead) throws Exception{
  try {
    inStream.currentOffset = 0;
    in.read(inStream.buffer, 0, forceRead);
  } catch (Exception e) {
    e.printStackTrace();
    System.out.println("ERROR FILLINSTREAM");
    disconnected = true;
  }
}[/code]

