#include <stdio.h>

int userguess() {
	printf("Guess a number: ");
	int enval;
	scanf("%d", &enval);
	return enval;
}

void game(int numb) {
	int gamov = 0;
	while (gamov != 1) {
		int gunum = userguess();
		if (gunum > numb) {
			printf("Too high!");
		} else if (gunum < numb) {
			printf("Too low!\n");
		} else {
			printf("Correct!");
			gamov = 1;
		}
		printf("\n");
	}
}

int main() {
	srand(time(NULL));
	game(rand() % 50);
	return 0;
}
