procedure goToSearchEngine;
begin
 SendKeys(searchEngine);
 wait(500);
 SendKeys(Chr(13));
 Writeln('Opened Google.');
end;

procedure search;
begin
 SendKeys(searchQuery);
 wait(500);
 SendKeys(Chr(13));
 Writeln('Searching...');
 wait(1000);
 Writeln('Search done! Thank you for using this script!');
end;

begin
 if(browser=1)Then
 begin
  openIE;
  wait(5000);
  findIEAddressBar;
 end;
 if(browser=2)Then
 begin
  openFF;
  wait(5000);
  findFFAddressBar;
 end;
 wait(500);
 goToSearchEngine;
 wait(2000);
 search;
end.[/scar]

