package simplicity;

import java.io.*;
import java.net.*;

public class Connection implements Runnable {
    public Connection(Socket socket) {
        mySocket = socket;

        thread = new Thread(this);

        thread.start();
    }

    @Override
    public void run() {
        try {
            output = mySocket.getOutputStream();

            do {
                output.write(value);
	System.out.print("Sent value: " + value + ".\n");

	try {
	    Thread.sleep(1000);

	    MainServer.terminationNeeded = true;
	} catch(InterruptedException ie) {
	    ie.printStackTrace();
	}
		} while(!MainServer.terminationNeeded);

            close();
        } catch(IOException ioe) {
			ioe.printStackTrace();

		}	
    }

    private void close() {
        try{
            output.close();
            mySocket.close();

            output = null;
            mySocket = null;
        } catch(IOException ioe) {
            ioe.printStackTrace();
        }
    }

    private Socket mySocket = null;
    private OutputStream output = null;
    private Thread thread = null;

    private int value = 25;
}[/code]

