public class MainServer {

	public MainServer() {
        try {
            server = new ServerSocket(Constants.serverPort, 1, null);

            System.out.print("Server is now listening on port "+ Constants.serverPort +".\n");

            while(!terminationNeeded){
                socket = server.accept();
	connection = new Connection(socket);

	 try {
