/**
 * @author RandQm
 * Version 1.0
 */

public class IdentifyHerbs {

	public static void Identify(int item, int slot, int ID) {
		client c = (client) PlayerHandler.players[ID];
		if (item != c.playerItems[slot] - 1){
			return;
		}
		if (c.playerHasItem(item)) {
			switch (item) {

			/* Required Level, Experience Added, Item Deleted, Item Added, Herb name */
			/* Note: this are the runescape level and ids, so you need lvl 3 for the first herb */

			case 199:
				c.identifyHerb(3, 3, 199, 249, "guam");
				break;
			case 201:
				c.identifyHerb(5, 4, 201, 251, "marrentil");
				break;
			case 203:
				c.identifyHerb(11, 5, 203, 253, "tarromin");
				break;
			case 205:
				c.identifyHerb(20, 6, 205, 255, "harralander");
				break;
			case 207:
				c.identifyHerb(25, 7, 207, 257, "rannar");
				break;
			case 3049:
				c.identifyHerb(30, 8, 3049, 2998, "toadflax");
				break;
			case 209:
				c.identifyHerb(40 , 9, 209, 259, "irit");
				break;
			case 211:
				c.identifyHerb(48, 10, 211, 261, "avantoe");
				break;
			case 213:
				c.identifyHerb(54, 11, 213, 263, "kwuarm");
				break;
