    private int requiredLevel;
    private int price;

    private Rock(int requiredLevel, int price) {
        this.requiredLevel = requiredLevel;
        this.price = price;
    }

    public int getRequiredLevel() {
        return requiredLevel;
    }

    public int getPrice() {
        return price;
    }

    @Override
    public String toString() {
        return name().toLowerCase();
    }
}
