Procedure ClickBMPS; Begin wait(300+random(32)) movemousesmooth(x,y) wait(32+random(16)) clickmouse(x,y,true) End;
Procedure FindBMPS1; Begin if(findBitmap(Mainpage,x,y))then Begin ClickBMPS; End; End;
Procedure FindBMPS2; Begin if(findBitmap(Low,x,y))then Begin ClickBMPS; End; End;
Procedure FindBMPS3; Begin if(findBitmap(World,x,y))then Begin ClickBMPS; End; End;
Procedure FindBMPS4; Begin if(findBitmap(user,x,y))then Begin ClickBMPS; End; End;
Procedure UsernamePassword; Begin Wait(500+random(160)); SendKeysSilent(Username); Wait(500+random(160)); SendKeysSilent(chr(13)); Wait(500+random(160)); SendKeysSilent(Password); Wait(100+random(36)); End;
Procedure FindBMPS5; Begin if(findBitmap(login,x,y))then Begin ClickBMPS; End; End;
Procedure FindBMPS6; Begin Wait(3000) if(findBitmap(clicktoplay,x,y))then Begin ClickBMPS; End; End;
Begin Loadbitmaps; Repeat FindBMPS1; Until(FindBitmap(Mainpage, x, y)) FindBMPS2; Until(FindBitmap(Low, x, y)) FindBMPS3; Until(FindBitmap(World, x, y)) FindBMPS4; Until(FindBitmap(user, x, y)) UsernamePassword; FindBMPS5; Until(FindBitmap(login, x, y)) FindBMPS6; Until(FindBitmap(clicktoplay, x, y)) End.[/code]
