function GetTreeExp(willow: string): Extended; //Enter the name of the tree where it says TreeName
var
  TreeXP: Extended;
begin
  case LowerCase(willow) of
    'tree': TreeXP := 25;
    'oak': TreeXP := 37.5;
    'willow': TreeXP := 62.5;
    'maple': TreeXP := 100;
    'mahogany': TreeXP := 125;
    'yew': TreeXP := 175;
    'magic': TreeXP := 250;
  end;
  Result := TreeXP;
end;

