[code]
import java.net.Socket;
import java.io.*;

public class Main {

	private Socket ircSocket = null;
	private BufferedWriter out = null;

	public void connect(String ircServer, int ircPort, String usrn, String channel){
		try{
			ircSocket = new Socket(ircServer, ircPort);
			out = new BufferedWriter(new OutputStreamWriter(ircSocket.getOutputStream()));
			out.write("USER " + usrn + "\n");
			out.write("JOIN " + channel + "\n");
		} catch(IOException l){
			System.out.println("Error");
		}	
	}

	public static void main(String[] args){
		Main main = new Main();
		String server = "irc.freenode.net";
		int port = 6667;
		String username = "lolclient";
		String channel = "#loltest";

		main.connect(server, port, username, channel);
	}

}

