[center][b]IP-BLOCKER[/b][/center]
[b]This script allows you to block diffrent IP Adresses!!! ;D[/b]
[i]Paste this into the [tt]<body>[/tt] of the site:[/i]
[code]  <SCRIPT LANGUAGE="JavaScript">


function ban(){
var ip = '<!--#echo var="REMOTE_ADDR"-->';

//Here you can place as much addresses as you want
if (ip == "xx.xx.xx.xx" || ip == "x.x.x.x") {

//here place the url and alert-window text that the banned person gets to see
alert("you are so...\nBANNED!");
history.go(-1);
window.location.replace("http://www.NEW URL HERE.com/")
}
}
ban();

</script>[/code]
