[code=java5]import java.awt.BorderLayout;
import java.awt.Color;
import java.awt.Font;
import java.awt.Graphics;
import java.awt.Graphics2D;
import java.awt.Image;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.awt.event.KeyAdapter;
import java.awt.event.KeyEvent;
import java.io.BufferedWriter;
import java.io.File;
import java.io.FileWriter;
import javax.swing.ImageIcon;
import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JPanel;
import javax.swing.Timer;

public class Game implements ActionListener {

    static JFrame frame;
    static DrawPanel panel;
    static Player player;
    static Enemy enemy;
    static File list;
    static Image bkg;
    static Image playerImg;
    static Image enemyImg;
    static Font font;

    int score;
    String scoreS;
    String playerCoords;
    String enemyCoords;
    boolean gameOver = false;
    int i;

    Timer time;

    //Game constructor
    //Sets up the JFrame and adds the Draw Panel
    //Makes a new player object, enemy object, and check collision object
    public Game() {

        frame = new JFrame("Avoid the Square");
        panel = new DrawPanel();
        player = new Player(0,0);
        enemy = new Enemy(450,450);

        list = new File("High_Score_List.txt");

        ImageIcon i1 = new ImageIcon("bkg1.png");
        ImageIcon i2 = new ImageIcon("player.png");
        ImageIcon i3 = new ImageIcon("bolder.png");

        bkg = i1.getImage();
        playerImg = i2.getImage();
        enemyImg = i3.getImage();

        font = new Font("arial", Font.BOLD, 14);

    }

    //Main method
    //Calls the go method
    public static void main(String[] args) {

        Game gui = new Game();
        gui.start();

    }

    public void start() {

        panel.addKeyListener(new KeyEventHandler());
        panel.setFocusable(true);

        frame.setSize(500,500);
        frame.getContentPane().add(BorderLayout.CENTER, panel);
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.setVisible(true);

        time = new Timer(5, this);
        time.start();

    }

    //actionPerformed method
    //makes actions like moving the player, ect. be called
    public void actionPerformed(ActionEvent e) {

        player.move();
        enemy.follow();
        panel.repaint();

        if(gameOver) {

            generateHighScoreList(score);

            time.stop();
            try {Thread.sleep(5000);} catch (Exception ex) {}
            System.exit(0);

        }

    }

    //sends the highscore to the file
    public void generateHighScoreList(int score) {

        String sScore = Integer.toString(score);

        try {

            BufferedWriter hslWriter = new BufferedWriter(new FileWriter(list, true));
            hslWriter.write("Score: " + sScore);
            hslWriter.newLine();
            hslWriter.close();

        } catch (Exception e) {

            System.out.println(e);

        }

    }

    public int getScroll() {

        if(i == 1100) {

            i = 0;

        } else {

            i++;

        }

        return i;

    }

    //DrawPanel class
    private class DrawPanel extends JPanel {

        //paint method
        //Game objects, score, ect is drawn here
        @Override
        public void paintComponent(Graphics g) {

            Graphics2D g2d = (Graphics2D) g;

            g2d.drawImage(bkg, 0, 0-getScroll(),null);

            g2d.drawImage(playerImg, Player.getX(), Player.getY(), null);
            g2d.drawImage(enemyImg, Enemy.getX(), Enemy.getY(), null);

            g2d.setColor(Color.green);
            g2d.setFont(font);

            scoreS = "Score: " + score;
            g.drawString(scoreS, 0, 10);

            playerCoords = "Player: (" + Player.getX() + "," + Player.getY() + ")";
            enemyCoords = "Enemy: (" + Enemy.getX() + "," + Enemy.getY() + ")";

            g.drawString(playerCoords, 0, 25);
            g.drawString(enemyCoords, 0, 40);

            if(player.getPlayer().intersects(enemy.getEnemy())) {

                /*
                 * Test Code for opening a new interface
                 *
                JFrame highscoreFrame = new JFrame("You Lost!");
                JLabel label1 = new JLabel();

                label1.setText(scoreS);

                highscoreFrame.setSize(200,200);
                highscoreFrame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
                highscoreFrame.getContentPane().add(BorderLayout.CENTER, label1);
                highscoreFrame.setVisible(true);
                 *
                 */


                g.setColor(Color.red);
                g.drawString("YOU LOST!", 200, 200);
                gameOver = true;

            } else {

                score++;
                gameOver = false;

            }

        }

    }

    //AL class
    private class KeyEventHandler extends KeyAdapter {

        //keyPressed method
        //calls the keyPressed method in the player class
        @Override
        public void keyPressed(KeyEvent e) {

            player.keyPressed(e);

        }

        //keyReleased method
        //calls the keyReleased method in the player class
        @Override
        public void keyReleased(KeyEvent e) {

            player.keyReleased(e);

        }

    }

}[/code]

[code=java5]import java.awt.Rectangle;
import java.awt.event.KeyEvent;

public class Player {

    //Rectangle to represent the player
    Rectangle player;

    //Variables to hold position and change in position
    static int x;
    static int y;
    int dx;
    int dy;

    //Player consructor
    //sets the xy and y of the player
    //makes the rectangle at the the players x and y and sets a width and height
    public Player(int x, int y) {

        Player.x = x;
        Player.y = y;

        player = new Rectangle(getX(),getY(),25,25);

    }

    public Player() {

        //do nothing

    }

    //returns the x coordinate of the player
    public static int getX() {

        return x;

    }

    //returns the y coordinate of the player
    public static int getY() {

        return y;

    }

    //returns the rectangle representing the player
    public Rectangle getPlayer() {

        return player;

    }

    //moves the player
    public void move() {

        if(x < 0) {

            dx = 0;
            x = 0;

        } else if(y < 0) {

            dy = 0;
            y = 0;

        } else if(x > 460) {

            dx = 0;
            x = 460;

        } else if(y > 436) {

            dy = 0;
            y = 436;

        } else {

            x += dx;
            y += dy;

        }

        player.setLocation(x, y);

    }

    //keyPressed method
    //Handles what happens when the arrow keys are pressed
    public void keyPressed(KeyEvent e) {

        int key = e.getKeyCode();

        switch(key) {

            case KeyEvent.VK_UP: dy = -2; break;
            case KeyEvent.VK_DOWN: dy = 2; break;
            case KeyEvent.VK_RIGHT: dx = 2; break;
            case KeyEvent.VK_LEFT: dx = -2; break;

        }

    }

    //KeyReleased method
    //Handles what happens when the arro keys are released
    public void keyReleased(KeyEvent e) {

        int key = e.getKeyCode();

        switch(key) {

            case (KeyEvent.VK_UP): dy = 0;
            case (KeyEvent.VK_DOWN): dy = 0;
            case (KeyEvent.VK_RIGHT): dx = 0;
            case (KeyEvent.VK_LEFT): dx = 0;

        }
    }

}[/code]

[code=java5]import java.awt.Rectangle;

public class Enemy {

    //Rectangle representing the square
    Rectangle EnemyPlayer;

    //object of the player class
    Player player;

    //ints for position and change in position
    static int x;
    static int y;
    int dx;
    int dy;

    //ints for getting the players coordinates
    int px;
    int py;

    //Enemy constuctor
    //sets the x and y coordinates and makes the rectangle representing the enemy
    //makes an object out of the player
    public Enemy(int x, int y) {

        Enemy.x = x;
        Enemy.y = y;

        EnemyPlayer = new Rectangle(getX(),getY(),50,50);

        player = new Player();

    }

    //blank constuctor
    public Enemy() {

        //do nothing

    }

    //returns the x coordinate of the enemy
    public static int getX() {

        return x;

    }

    //returns the y coordinate of the enemy
    public static int getY() {

        return y;

    }

    //returns the rectangle representing the enemy
    public Rectangle getEnemy() {

        return EnemyPlayer;

    }

    //follow method
    public void follow() {

        //get the players coordinates
        px = Player.getX();
        py = Player.getY();

        //x coordinates
        if(px > x) {

            dx = 1;
            x += dx;

        } else if (px < x) {

            dx = -1;
            x += dx;

        } else {

            dx = 0;
            //do nothing

        }

        //y coordinates
        if(py > y) {

            dy = 1;
            y += dy;

        } else if (py < y) {

            dy = -1;
            y += dy;

        } else {

            dy = 0;
            //do nothing

        }

        EnemyPlayer.setLocation(x, y);

    }

}[/code]

