[code]import java.awt.*;
import java.awt.event.*;
import java.applet.Applet;
import javax.swing.*;

public class test extends Applet {
    JButton Start;
    JScrollBar scrollbar_v_1;
    JList list_1;
    JScrollPane sp_list_1;

    public void init() {
        testLayout customLayout = new testLayout();

        setFont(new Font("Helvetica", Font.PLAIN, 12));
        setLayout(customLayout);

        Start = new JButton("Start");
        add(Start);

        scrollbar_v_1 = new JScrollBar(Scrollbar.VERTICAL);
        add(scrollbar_v_1);

        DefaultListModel listModel_list_1 = new DefaultListModel();
        listModel_list_1.addElement("item1");
        listModel_list_1.addElement("item2");
        list_1 = new JList(listModel_list_1);
        sp_list_1 = new JScrollPane(list_1);
        add(sp_list_1);

        setSize(getPreferredSize());

    }

    public static void main(String args[]) {
        test applet = new test();
        Frame window = new Frame("test");

        window.addWindowListener(new WindowAdapter() {
            public void windowClosing(WindowEvent e) {
                System.exit(0);
            }
        });

        applet.init();
        window.add("Center", applet);
        window.pack();
        window.setVisible(true);
    }
}

class testLayout implements LayoutManager {

    public testLayout() {
    }

    public void addLayoutComponent(String name, Component comp) {
    }

    public void removeLayoutComponent(Component comp) {
    }

    public Dimension preferredLayoutSize(Container parent) {
        Dimension dim = new Dimension(0, 0);

        Insets insets = parent.getInsets();
        dim.width = 302 + insets.left + insets.right;
        dim.height = 190 + insets.top + insets.bottom;

        return dim;
    }

    public Dimension minimumLayoutSize(Container parent) {
        Dimension dim = new Dimension(0, 0);
        return dim;
    }

    public void layoutContainer(Container parent) {
        Insets insets = parent.getInsets();

        Component c;
        c = parent.getComponent(0);
        if (c.isVisible()) {c.setBounds(insets.left+0,insets.top+0,144,56);}
        c = parent.getComponent(1);
        if (c.isVisible()) {c.setBounds(insets.left+280,insets.top+0,24,192);}
        c = parent.getComponent(2);
        if (c.isVisible()) {c.setBounds(insets.left+0,insets.top+80,176,88);}
    }
}
