[b]SpanishVerbConjugator.java[/b]

[code=java]public class SpanishVerbConjugator {

	public void start() {

		userInput input = new userInput();
		PresentTenseIndicative presentTenseIndicative = new PresentTenseIndicative();
		PreteritTenseIndicative preteritTenseIndicative = new PreteritTenseIndicative();
		ImperfectTenseIndicative imperfectTenseIndicative = new ImperfectTenseIndicative();
		FutureTenseIndicative futureTenseIndicative = new FutureTenseIndicative();

		System.out.println("------------------------------------------");
		System.out.println("| Tense:                     | Options:  |");
		System.out.println("------------------------------------------");
		System.out.println("| Present Tense Indicative   | present   |");
		System.out.println("| Preterit Tense Indicative  | preterit  |");
		System.out.println("| Imperfect Tense Indicative | imperfect |");
		System.out.println("| Future Tense Indicative    | future    |");
		System.out.println("------------------------------------------");
		System.out.println("");

		input.getUserInput("Enter the verb tense: ");
		System.out.println("");

		if (input.input.equalsIgnoreCase("Present")) {

			presentTenseIndicative.Conjugate();

		}

		if (input.input.equalsIgnoreCase("Preterit")) {

			preteritTenseIndicative.Conjugate();

		}

		if (input.input.equalsIgnoreCase("Imperfect")) {

			imperfectTenseIndicative.Conjugate();		

		}

		if (input.input.equalsIgnoreCase("Future")) {

			futureTenseIndicative.Conjugate();

		}

	}

}[/code]

[b]VerbTense.java[/b]

[code=java]abstract public class VerbTense {

	userInput input = new userInput();

	public String verb;
	public String yo;
	public String tu;
	public String UdElElla;
	public String nosotros;
	public String vosotros;
	public String UdsEllosEllas;

	public void getVerb() {

		input.getUserInput("Enter the verb: ");

		verb = input.input;
		yo = input.input;
		tu = input.input;
		UdElElla = input.input;
		nosotros = input.input;
		vosotros = input.input;
		UdsEllosEllas = input.input;

	}

	public abstract void Conjugate();

}[/code]

[b]PresentTenseIdicative.java[/b]

[code=java]public class  PresentTenseIndicative extends VerbTense{

	userInput input = new userInput();
	SpanishVerbConjugator conj = new SpanishVerbConjugator();

	public void Conjugate() {

		getVerb();

		if (verb.endsWith("ar")) {

			yo = yo.replaceAll("ar", "o");
			tu = tu.replaceAll("ar", "as");
			UdElElla = UdElElla.replaceAll("ar", "a");
			nosotros = nosotros.replaceAll("ar", "amos");
			vosotros = vosotros.replaceAll("ar", "áis");
			UdsEllosEllas = UdsEllosEllas.replaceAll("ar", "an");

		}

		if (verb.endsWith("er")) {

			yo = yo.replaceAll("er", "o");
			tu = tu.replaceAll("er", "es");
			UdElElla = UdElElla.replaceAll("er", "e");
			nosotros = nosotros.replaceAll("er", "emos");
			vosotros = vosotros.replaceAll("er", "éis");
			UdsEllosEllas = UdsEllosEllas.replaceAll("er", "en");

		}

		if (verb.endsWith("ir")) {

			yo = yo.replaceAll("ir", "o");
			tu = tu.replaceAll("ir", "es");
			UdElElla = UdElElla.replaceAll("ir", "e");
			nosotros = nosotros.replaceAll("ir", "imos");
			vosotros = vosotros.replaceAll("ir", "ís");
			UdsEllosEllas = UdsEllosEllas.replaceAll("ir", "en");

			}

		System.out.println("");
		System.out.println("Conjugation of the verb: " + verb);
		System.out.println("");
		System.out.println("Yo: " + yo);
		System.out.println("Tú: " + tu);
		System.out.println("Usted, Él, Ella: " + UdElElla);
		System.out.println("Nosotros: " + nosotros);
		System.out.println("Vosotros: " + vosotros);
		System.out.println("Ustedes, Ellos, Ellas: " + UdsEllosEllas);
		System.out.println("");

		conj.start();

	}

}[/code]

