		getVerb();

		if (verb.endsWith("ar")) {

			yo = yo.replaceAll("ar", "é");
			tu = tu.replaceAll("ar", "aste");
			UdElElla = UdElElla.replaceAll("ar", "ó");
			nosotros = nosotros.replaceAll("ar", "amos");
			vosotros = vosotros.replaceAll("ar", "asteis");
			UdsEllosEllas = UdsEllosEllas.replaceAll("ar", "aron");


		}

		if (verb.endsWith("er")) {

			yo = yo.replaceAll("er", "í");
			tu = tu.replaceAll("er", "iste");
			UdElElla = UdElElla.replaceAll("er", "ió");
			nosotros = nosotros.replaceAll("er", "imos");
			vosotros = vosotros.replaceAll("er", "isteis");
			UdsEllosEllas = UdsEllosEllas.replaceAll("er", "ieron");

		}

		if (verb.endsWith("ir")) {

			yo = yo.replaceAll("ir", "í");
			tu = tu.replaceAll("ir", "iste");
			UdElElla = UdElElla.replaceAll("ir", "ió");
			nosotros = nosotros.replaceAll("ir", "imos");
			vosotros = vosotros.replaceAll("ir", "isteis");
			UdsEllosEllas = UdsEllosEllas.replaceAll("ir", "ieron");

		}

		System.out.println("");
		System.out.println("Conjugation of the verb: " + input.input);
		System.out.println("");
